import React from 'react'
import './App.css';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      api: '',
    };
  }

  async componentDidMount() {
    try {
      const url = `https://api.randomuser.me/`;
      const response = await fetch(url)
      const dataJson = await response.json()
      this.setState({ api: dataJson.results})
    } catch (error) {
      console.log(error)
    }
  }

  render() {
    const { cel } = this.state.api
    return (
      <div className="Body">
        {cel}
      </div>
    );
  }
}

export default App;
